package.patch:
cd linux-3.15 unxz -c ${SRCDIR}/patch-${PKGVERSION}.xz | patch -p1 cp -v ${SRCDIR}/lesslinux-fblogo-20100922.ppm drivers/video/logo/logo_linux_clut224.ppm cp -v ${SRCDIR}/config-3.15-amd64-lesslinux-20140618-1030 .config sed -i 's/lesslinux-000/lesslinux-amd64-000/g' .config echo 'CONFIG_ACPI_PROCFS_POWER=y' >> .config
# sed -i 's/# CONFIG_IA32_EMULATION is not set/CONFIG_IA32_EMULATION=y/g' .config # echo 'CONFIG_IA32_EMULATION=y' >> .config # sed -i 's/# CONFIG_COMPAT_VDSO is not set/CONFIG_COMPAT_VDSO=y/g' .config # echo 'CONFIG_COMPAT_VDSO=y' >> .config # sed -i 's/# CONFIG_X86_X32 is not set/CONFIG_X86_X32=y/g' .config # echo 'CONFIG_X86_X32=y' >> .config # echo 'CONFIG_IA32_AOUT=m' >> .config
cd .. mv linux-3.15 linux-${PKGVERSION}

package.install:

cd linux-${PKGVERSION}
localversion=` grep '^CONFIG_LOCALVERSION' .config | sed 's/"//g' | awk -F '=' '{print $2}' `
mkdir -p ${TGTDIR}/boot
export PATH=/usr/local/crosstools-amd64/bin:$PATH
INSTALL_MOD_PATH=${TGTDIR} make ARCH=x86_64 CROSS_COMPILE=amd64-linux-uclibc- modules_install
install -m 0644 arch/x86/boot/bzImage ${TGTDIR}/boot/vmlinuz-${PKGVERSION}${localversion}

## make ARCH=x86_64 CROSS_COMPILE=amd64-linux-uclibc- firmware_install	
## make install	

find ${TGTDIR}/lib/modules/${PKGVERSION}${localversion} -type f -name '*.ko' -exec amd64-linux-uclibc-strip --strip-unneeded {} \; 
rm -rf ${TGTDIR}/lib/firmware
