package.install:
cd musl-${PKGVERSION}
export PATH=/usr/local/crosstools-amd64-musl/bin:$PATH export CLFS_HOST=$(echo ${MACHTYPE} | sed "s/-[^-]*/-cross/") export CLFS_TARGET=x86_64-linux-musl export CLFS_CPU=k8
DESTDIR=${TGTDIR}/usr/local/crosstools-amd64-musl/${CLFS_TARGET} make install || exit 1 ${CLFS}/cross-tools/bin/${CLFS_TARGET}-strip ${TGTDIR}/usr/local/crosstools-amd64-musl/${CLFS_TARGET}/lib/*.so* ${CLFS}/cross-tools/bin/${CLFS_TARGET}-strip ${TGTDIR}/usr/local/crosstools-amd64-musl/${CLFS_TARGET}/lib64/*.so*
