package.install:
cd ${PKGNAME}-${PKGVERSION}
export PATH=/usr/local/crosstools-ia32-musl/bin:$PATH export CLFS_HOST=$(echo ${MACHTYPE} | sed "s/-[^-]*/-cross/") export CLFS_TARGET=i386-linux-musl export CLFS_CPU=i686
DESTDIR=${TGTDIR}/usr/local/crosstools-ia32-musl/${CLFS_TARGET} make install || exit 1 ${CLFS}/cross-tools/bin/${CLFS_TARGET}-strip ${TGTDIR}/usr/local/crosstools-ia32-musl/${CLFS_TARGET}/lib/*.so* ${CLFS}/cross-tools/bin/${CLFS_TARGET}-strip ${TGTDIR}/usr/local/crosstools-ia32-musl/${CLFS_TARGET}/lib64/*.so*
